import React, { Component } from "react";
import "../App.css";
import Buttons from "../components/button";
import Inputs from "../components/input";
export default class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      Username: "",
      Password: ""
    };
    this.onChange = this.onChange.bind(this)

  }

  onChange(e) {
    console.log('name '+this.state.Username)
    this.setState({ [e.target.name]: e.target.value })
}
  render() {
    return (
      <div class="Login">
      <form noValidate onSubmit={this.onSubmit}>
        <div id="header">
          <h1>Welcome to the Login Page</h1>
        </div>
        <Inputs className = {"form-control"} id={"userName"} name={this.state.Username} placeholder={"Username"} value={this.state.Username} onChange={this.onChange}/>
        <Inputs className = {"form-control"} id={"password"} name={this.state.Password} placeholder={"Password"} value={this.state.Password} onChange={this.onChange}/>
        <Buttons />
        </form>
      </div>
    );
  }
}
