import React, { Component } from "react";
import * as Bootstrap from "react-material-ui";
export default class Inputs extends Component {
  constructor(props) {
    super(props);
    this.state = {
        Username: "",
        Password: ""
    };
}
      handleUsernameChange = event => {
        const Username = event.target.value;
        this.setState({ Username: Username });
    }
    handlePasswordChange = event => {
        const Password = event.target.value;
        this.setState({ Password: Password });
    }
    render() {
      return (
      <div>
        <div id="Email">
          <Bootstrap.Form.Group>
            <Bootstrap.Form.Control type="email" placeholder="Enter Email" name ="Username" value={this.state.Username}
            onChange={this.handleUsernameChange}
            autoComplete="Username"/>
          </Bootstrap.Form.Group>
        </div>

        <div id="Password">
          <Bootstrap.Form.Group>
            <Bootstrap.Form.Control type="password" placeholder="Password" name = "Password"  value={this.state.Password}
            onChange={this.handlePasswordChange}/>
          </Bootstrap.Form.Group>
        </div>

      </div>
    );
  }
}
