import React, { Component } from "react";
import * as Bootstrap from "react-bootstrap";
import "../App.css";
export default class Login extends Component {
  constructor(...args) {
    super(...args);

    this.state = { validated: false };
  }

  handleSubmit(event) {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }
    this.setState({ validated: true });
  }
  render() {
    const { validated } = this.state;
    return (
      <div class = "Login">
      <div id="header">
        <h1>Welcome to the Login Page</h1>
        </div>
        <Bootstrap.Form  
        noValidate
        validated={validated}
        onSubmit={e => this.handleSubmit(e)}>

          <div id="Email">
            <Bootstrap.Form.Group as={Col} md="4" controlId="validationCustomUsername">
              <Bootstrap.Form.Control
                type="email"
                placeholder="Enter Email" required
              />
              <Bootstrap.Form.Control.Feedback type="invalid">
                Please choose a username.
              </Bootstrap.Form.Control.Feedback>
            </Bootstrap.Form.Group>
          </div>
          <div id="Password">
            <Bootstrap.Form.Group controlId="formBasicPassword">

              <Bootstrap.Form.Control
                type="password"
                placeholder="Password"
              />
            </Bootstrap.Form.Group>
          </div>
          <div id="Submit">
            <Bootstrap.Button variant="outline-dark" type="submit">
              Submit
            </Bootstrap.Button>
          </div>
        </Bootstrap.Form>
        </div>

    );
  }
}
