import React, { Component } from "react";
import "../App.css";
import { userLogin } from "../services/userServices";
import Buttons from "../components/button";
import Inputs from "../components/input";
import Snackbar from "@material-ui/core/Snackbar";
import { FormGroup, FormControlLabel, FormControl } from "@material-ui/core";

export default class Register extends Component {
    constructor(props) {
        super(props);
        this.state = {
            firstName: "",
            lastName: "",
            Email: "",
            Password: "",
            confirmPassword: "",
            snackBarMessage: ""
        };
        this.baseState = this.state;
    }

    handleuserfirstNameChange = event => {
        const firstName = event.target.value;
        this.setState({ firstName: firstName });
    };

    handleuserlastNameChange = event => {
        const lastName = event.target.value;
        this.setState({ lastName: lastName });
    };

    handleuserEmailChange = event => {
        const Email = event.target.value;
        this.setState({ Email: Email });
    };

    handlePasswordChange = event => {
        const Password = event.target.value;
        this.setState({ Password: Password });
    };

    handleconfirmPasswordChange = event => {
        const confirmPassword = event.target.value;
        this.setState({ confirmPassword: confirmPassword });
    };
render() {
    return (
        <div class = "Register">
        <form onSubmit={this.handleSubmit}>
        <FormGroup controlId="email" bsSize="large">
          <FormControlLabel>Email</FormControlLabel>
          <FormControl
            autoFocus
            type="email"
            value={this.state.email}
            onChange={this.handleChange}
          />
        </FormGroup>
        <FormGroup controlId="password" bsSize="large">
          <FormControlLabel>Password</FormControlLabel>
          <FormControl
            value={this.state.password}
            onChange={this.handleChange}
            type="password"
          />
        </FormGroup>
        <FormGroup controlId="confirmPassword" bsSize="large">
          <FormControlLabel>Confirm Password</FormControlLabel>
          <FormControl
            value={this.state.confirmPassword}
            onChange={this.handleChange}
            type="password"
          />
        </FormGroup>
        </form>
        </div>
    )
}
}
